drop table if exists card_level_spec cascade;

create table card_level_spec(
	card_id 		uuid 	  not null,
	level 			int  	  not null,
	hp 				int  	  not null,
	hit 			int  	  not null,
	critical 		int  	  not null,
	dodge 			int  	  not null,
	resist 			int  	  not null,
	block 			int  	  not null,
	armor 			int  	  not null,
	atk_range 		int4range not null,
	agility 		int 	  not null,
	frags_required 	int 	  not null,
	coins_required 	int 	  not null
);

